                        public function ConnectTargetToWorkstation($targetClass, $targetId, $updateLocation)
{
    if ($targetId != 0) {
        $oTargetObject = MetaModel::GetObject($targetClass, $targetId, false);
        if (is_object($oTargetObject)) {
            $oTargetObject->Set('workstation_id', $this->GetKey());
            if ($updateLocation)
                $oTargetObject->Set('location_id', $this->Get('location_id'));
            $oTargetObject->DBUpdate();
        }
    }
}

                        public function DisconnectTargetFromWorkstation($targetClass, $targetId)
{
    if ($targetId != 0) {
        $oTargetObject = MetaModel::GetObject($targetClass, $targetId, false);
        if (is_object($oTargetObject)) {
            $oTargetObject->Set('workstation_id', '');
            $oTargetObject->DBUpdate();
        }
    }
}

                        public function AfterInsert()
{
    $sUpdateLocations = MetaModel::GetConfig()->GetModuleSetting('br-peripherals', 'update_location', 'false');
    $bUpdateLocations = filter_var($sUpdateLocations, FILTER_VALIDATE_BOOLEAN);

    if ($this->Get('pc_id') != 0)
        $this->ConnectTargetToWorkstation('PC', $this->Get('pc_id'), $bUpdateLocations);
    if ($this->Get('dockingstation_id') != 0)
        $this->ConnectTargetToWorkstation('DockingStation', $this->Get('dockingstation_id'), $bUpdateLocations);
    if ($this->Get('keyboard_id') != 0)
        $this->ConnectTargetToWorkstation('Keyboard', $this->Get('keyboard_id'), $bUpdateLocations);
    if ($this->Get('mouse_id') != 0)
        $this->ConnectTargetToWorkstation('Mouse', $this->Get('mouse_id'), $bUpdateLocations);
    if ($this->Get('headset_id') != 0)
        $this->ConnectTargetToWorkstation('Headset', $this->Get('headset_id'), $bUpdateLocations);
    parent::AfterInsert();
}

                        public function OnUpdate()
{
    $aChanges = $this->ListChanges();
    if (array_key_exists('pc_id', $aChanges)) {
        // record in the local memory object that the PC was changed
        $this->bPCChanged = true;
        // disconnect the old Workstation
        $this->DisconnectTargetFromWorkstation('PC',$this->GetOriginal('pc_id'));
    }
    if (array_key_exists('dockingstation_id', $aChanges)) {
        // record in the local memory object that the DockingStation was changed
        $this->bDockingStationChanged = true;
        // disconnect the old Workstation
        $this->DisconnectTargetFromWorkstation('DockingStation',$this->GetOriginal('dockingstation_id'));
    }
    if (array_key_exists('keyboard_id', $aChanges)) {
        // record in the local memory object that the Keyboard was changed
        $this->bKeyboardChanged = true;
        // disconnect the old Workstation
        $this->DisconnectTargetFromWorkstation('Keyboard',$this->GetOriginal('keyboard_id'));
    }
    if (array_key_exists('mouse_id', $aChanges)) {
        // record in the local memory object that the Mouse was changed
        $this->bMouseChanged = true;
        // disconnect the old Workstation
        $this->DisconnectTargetFromWorkstation('Mouse',$this->GetOriginal('mouse_id'));
    }
    if (array_key_exists('headset_id', $aChanges)) {
        // record in the local memory object that the Headset was changed
        $this->bHeadsetChanged = true;
        // disconnect the old Workstation
        $this->DisconnectTargetFromWorkstation('Headset',$this->GetOriginal('headset_id'));
    }
    // change locations on assigned objects when location of workstation is changed
    if (array_key_exists('location_id', $aChanges)) {
        // record in the local memory object that the Location was changed
        $this->bLocationChanged = true;
    }
    parent::OnUpdate();
}

                        public function AfterUpdate()
{
    $sUpdateLocations = MetaModel::GetConfig()->GetModuleSetting('br-peripherals', 'update_location', 'false');
    $bUpdateLocations = filter_var($sUpdateLocations, FILTER_VALIDATE_BOOLEAN);

    // The Workstation is updated in DB and assigned Peripherals changed
    if (isset($this->bPCChanged))
        $this->ConnectTargetToWorkstation('PC', $this->Get('pc_id'), $bUpdateLocations);
    if (isset($this->bDockingStationChanged))
        $this->ConnectTargetToWorkstation('DockingStation', $this->Get('dockingstation_id'), $bUpdateLocations);
    if (isset($this->bKeyboardChanged))
        $this->ConnectTargetToWorkstation('Keyboard', $this->Get('keyboard_id'), $bUpdateLocations);
    if (isset($this->bMouseChanged))
        $this->ConnectTargetToWorkstation('Mouse', $this->Get('mouse_id'), $bUpdateLocations);
    if (isset($this->bHeadsetChanged))
        $this->ConnectTargetToWorkstation('Headset', $this->Get('headset_id'), $bUpdateLocations);
    // change locations on assigned objects when location of workstation is changed
    if (isset($this->bLocationChanged) && $bUpdateLocations)
    {
        $aPeripherals = array(array('PC','pc_id'),array('DockingStation','dockingstation_id'),array('Keyboard','keyboard_id'),array('Mouse','mouse_id'),array('Headset','headset_id'));
        foreach ($aPeripherals as $aPeripheral) {
            $this->ConnectTargetToWorkstation($aPeripheral[0], $this->Get($aPeripheral[1]), $bUpdateLocations);
        }
    }
    parent::AfterUpdate();
}

                        public function OnDelete()
{
    $aChanges = $this->ListChanges();
    if (array_key_exists('pc_id', $aChanges))
        $this->DisconnectTargetFromWorkstation('PC', $this->Get('pc_id'));
    if (array_key_exists('dockingstation_id', $aChanges))
        $this->DisconnectTargetFromWorkstation('DockingStation', $this->Get('dockingstation_id'));
    if (array_key_exists('keyboard_id', $aChanges))
        $this->DisconnectTargetFromWorkstation('Keyboard', $this->Get('keyboard_id'));
    if (array_key_exists('mouse_id', $aChanges))
        $this->DisconnectTargetFromWorkstation('Mouse', $this->Get('mouse_id'));
    if (array_key_exists('headset_id', $aChanges))
        $this->DisconnectTargetFromWorkstation('Headset', $this->Get('headset_id'));
    parent::OnDelete();
}

                        public function GetAttributeFlags($sAttCode, &$aReasons = array(), $sTargetState = '')
					    {
						    // HACK: Make workstation_id field readonly to prevent update loops from Workstation
						    if (($sAttCode == 'workstation_id'))
							    return(OPT_ATT_READONLY | parent::GetAttributeFlags($sAttCode, $aReasons, $sTargetState));
						    return parent::GetAttributeFlags($sAttCode, $aReasons, $sTargetState);
					    }

